import React, { useState } from "react";

import FriendCard from "./FriendCard";
import Card from "../UI/Card/Card";
import classes from "./AllFriendsInfo.module.css";

const AllFriendsInfo = () => {
  const [allFriends, setAllFriends] = useState([]);
  const fetchAllFriends = async () => {
    try {
      const response = await fetch("http://localhost:8080/getallusers");
      if (!response.ok) {
        throw new Error("Something went wrong");
      }
      const body = await response.json();
      // console.log(body);
      setAllFriends(body);
    } catch (error) {
      console.log(error.message);
    }
  };
  fetchAllFriends();
  const allFriendsCard = allFriends.map((friend) => (
    <FriendCard
      key={friend.id}
      name={friend.name}
      email={friend.email}
      birthday={friend.birthday}
      city={friend.city}
    />
  ));
  return (
    <Card className={classes.friendsinfo}>
      <h1>All friends</h1>
      <ul className={classes.friendlist}>{allFriendsCard}</ul>
    </Card>
  );
};

export default AllFriendsInfo;
