import React, { useState, useEffect } from "react";

import FriendCard from "./FriendCard";
import Card from "../UI/Card/Card";
import classes from "./AllFriendsInfo.module.css";

const AllFriendsInfo = () => {
  const [allFriends, setAllFriends] = useState([]);
  useEffect(() => {
    const fetchAllFriends = async () => {
      const reqOptions = {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          id: localStorage.getItem("isLoggedIn"),
        }),
      };
      try {
        const response = await fetch(
          "http://localhost:8080/getallusers",
          reqOptions
        );
        if (!response.ok) {
          throw new Error("Something went wrong");
        }
        const body = await response.json();
        setAllFriends(body);
      } catch (error) {
        console.log(error.message);
      }
    };
    fetchAllFriends();
  }, []);

  const addFriend = (id) => {
    console.log(id);
  };

  const allFriendsCard = allFriends.map((friend) => (
    <FriendCard
      key={friend.id}
      id={friend.id}
      name={friend.name}
      email={friend.email}
      birthday={friend.birthday}
      city={friend.city}
      button={"Add friend"}
      isFriend={friend.isFriend}
      addFriend={addFriend}
    />
  ));
  return (
    <Card className={classes.friendsinfo}>
      <h1>All people</h1>
      <ul className={classes.friendlist}>{allFriendsCard}</ul>
    </Card>
  );
};

export default AllFriendsInfo;
