import React, { useState, useEffect } from "react";

import classes from "./MyFriendsInfo.module.css";

const MyFriendsInfo = () => {
  const [allFriendsIds, setAllFriendsIds] = useState([0]);
  useEffect(() => {
    const fetchAllFriendsId = async () => {
      const reqOptions = {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          id: localStorage.getItem("isLoggedIn"),
        }),
      };
      try {
        const response = await fetch(
          "http://localhost:8080/getallmyfriendsid",
          reqOptions
        );
        if (!response.ok) {
          throw new Error("Something went wrong");
        }
        const body = await response.json();
        // console.log(body);
        let oldFriends = [];
        for (let i = 0; i < body.length; i++) {
          oldFriends.push(body[i].friendsid);
          // setAllFriendsIds((old) => [...old, body[i].friendsid]);
        }
        setAllFriendsIds(oldFriends);
      } catch (error) {
        console.log(error.message);
      }
    };
    fetchAllFriendsId();
  }, []);
  console.log(allFriendsIds);
  useEffect(() => {
    const fetchActualFriends = async () => {
      const reqOptions = {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          friendsid: allFriendsIds,
        }),
      };
      try {
        const response = await fetch(
          "http://localhost:8080/getarrayoffriends",
          reqOptions
        );
        if (!response.ok) {
          throw new Error("Something went wrong");
        }
      } catch (error) {
        console.log(error.message);
      }
    };
    fetchActualFriends();
  }, [allFriendsIds]);

  return (
    <div className={classes.friendsinfo}>
      <h1>My friends</h1>
    </div>
  );
};

export default MyFriendsInfo;
