import React, { useState } from "react";

import Card from "../UI/Card/Card";
import SideBar from "./SideBar";
import ProfileInfo from "./ProfileInfo";
import MyFriendsInfo from "./MyFriendsInfo";
import classes from "./Home.module.css";

const Home = (props) => {
  const [activeInfo, setActiveInfo] = useState("profile");

  const getProfileInfo = () => {
    setActiveInfo("profile");
  };

  const getMyFriendsInfo = () => {
    setActiveInfo("myfriends");
  };

  return (
    <Card className={classes.home}>
      <SideBar />
      {activeInfo === "profile" && (
        <ProfileInfo onProfileInfo={getProfileInfo} />
      )}
      {activeInfo === "myfriends" && (
        <MyFriendsInfo onMyFriendsInfo={getMyFriendsInfo} />
      )}
    </Card>
  );
};

export default Home;
