import React, { useState } from "react";

import Card from "../UI/Card/Card";
import Button from "../UI/Button/Button";
import classes from "./Register.module.css";

const Register = () => {
  const [enteredName, setEnterName] = useState("");
  const [enteredEmail, setEnteredEmail] = useState("");
  const [enteredPassword, setEnteredPassword] = useState("");
  const [enteredCity, setEnteredCity] = useState("");
  const [enteredBirthday, setEnteredBirthday] = useState("");
  const [formIsValid, setFormIsValid] = useState(false);

  const nameIsValid = enteredName.trim() !== "";
  const emailIsValid = enteredEmail.trim() !== "";
  const passwordIsValid = enteredPassword.trim().length > 6;
  const cityIsValid = enteredCity.trim() !== 0;
  const birthdayIsValid = enteredBirthday.trim() !== null;

  if (
    nameIsValid &&
    emailIsValid &&
    passwordIsValid &&
    cityIsValid &&
    birthdayIsValid
  ) {
    setFormIsValid(true);
  }

  const nameInputChangeHandler = (event) => {
    setEnterName(event.target.value);
  };

  const emailInputChangeHandler = (event) => {
    setEnteredEmail(event.target.value);
  };
  const passwordInputChangeHandler = (event) => {
    setEnteredPassword(event.target.value);
  };
  const cityInputChangeHandler = (event) => {
    setEnteredCity(event.target.value);
  };
  const birthdayInputChangeHandler = (event) => {
    setEnteredBirthday(event.target.value);
  };

  return (
    <Card className={classes.register}>
      <form>
        <h1>Register</h1>
        <div className={classes["form-control"]}>
          <label>Name:</label>
          <input type="text" onChange={nameInputChangeHandler} />
        </div>
        <div className={classes["form-control"]}>
          <label>Email:</label>
          <input type="email" onChange={emailInputChangeHandler} />
        </div>
        <div className={classes["form-control"]}>
          <label>Password:</label>
          <input type="text" onChange={passwordInputChangeHandler} />
        </div>
        <div className={classes["form-control"]}>
          <label>City:</label>
          <input type="text" onChange={cityInputChangeHandler} />
        </div>
        <div className={classes["form-control"]}>
          <label>Birthday:</label>
          <input type="date" onChange={birthdayInputChangeHandler} />
        </div>
        <div className={classes["form-actions"]}>
          <Button disabled={formIsValid} className={classes.mybutton}>
            Submit
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Register;
